//main  加载所有资源,(组件:js/css/...)创建vue实例对象
import Vue from 'vue'
import App from './App.vue'
//1.加载路由模块
import router from './router'
//1.1:加载VueReseource
import VueResource from 'vue-resource'
//1.2:注册,将VueResource加载到实例对象vue
Vue.use(VueResource);
//1.2.1设置请求根路径(服务器地址)
Vue.http.options.root="http://127.0.0.1:3000/"
//1.2.2创建全局过滤器,
Vue.filter("datatimeFilter",function(val){
  var date=new Date(val);
  var y=date.getFullYear();
  var m=date.getMonth()+1;
  m<10&&(m="0"+m);
  var d=date.getDate();
  d<10&&(d="0"+d);
  var h=date.getHours();
  h>12&&(h-=12);
  h<10&&(h="0"+h);
  var M=date.getMinutes();
  M<10&&(M="0"+M);   
  return `${y}-${m}-${d} ${h}:${M}`;
})

//1.3:将mint-ui加载到项目里
//1.4:按需引入一个组件,顶部固定导航栏
//    按需引入组件Swipe,SwipeItem  图片轮播
//import {Header,Swipe,SwipeItem,Button,Lazyload} from 'mint-ui'
//1.5:注册组件
//Vue.component(Header.name,Header)  //注册Header
//Vue.component(Swipe.name,Swipe)    //注册Swipe
//Vue.component(SwipeItem.name,SwipeItem)   //注册SwipeItem
//Vue.component(Button.name,Button)
//Vue.component(Lazyload)

//引入完整的mint-ui
import MintUI from "mint-ui"
Vue.use(MintUI)

//1.6:引入mui的css
import "./lib/mui/css/mui.css"
import "./lib/mui/css/icons-extra.css"
//1.7引入mint-ui组件所需的样式文件
//默认查找文件路径
//node_modules/mint-ui/lib/style.css
import "mint-ui/lib/style.css"

//2.是否是生产模式  false
Vue.config.productionTip = false

//2.1将vuex添加到项目中
//-2.1.1导入vuex
import Vuex from "vuex"
//-2.1.2将vuex注册vue
Vue.use(Vuex)
//-2.1.3创建vuex实例[共享数据;提供操作数据的方法;获取数据的方法]
  //new Vuex.Store();的到数据仓储对象
var store=new Vuex.Store({
  state:{
    //#专门用来存储共享的数据--想象成组件data
    //如果在组件中操作  this.$store.state.***方法
    //购物车中数量共享数据
    count:0
  },
  mutations:{  
    //#对共享数据进行操作
    //操作共享数据只能通过 mutations提供的方法
    //如果组件想来调用方法 mutations只能用 this.$store.commit("increment")
    increment(state){state.count++},
    subtract(state){state.count--}
  },
  getters:{
    //#getters只负责对外提供数据,不负责修改数据
    //在组件模板中 {{$store.getters.optCount}}
    optCount:function(state){
      return state.count;
    }
  }
});
//-2.1.4将vuex实例对象添加vue对象,最下方里添加
  //小结:state中的数据,不能直接修改,必须通过单独定义的mutations
      //如果组件想直接获取数据 简单:this.$store.state.***
      //推荐使用 getters方法
      //如果组件先刚修改数据 this.$store.commit("subtract")

//3.创建一个vue实例对象挂载到public/index.html#app
new Vue({
  router,
  render: h => h(App),
  store  //vuex创建的实例对象
}).$mount('#app')
